DROP VIEW IF EXISTS `vista_nadadores_por_club`;\nCREATE VIEW `vista_nadadores_por_club` AS\nselect `c`.`nombre` AS `nombre_club`,`c`.`ciudad` AS `ciudad_club`,count(`n`.`id`) AS `total_nadadores`,group_concat(concat(`n`.`nombre`,' ',`n`.`apellidos`,' (',timestampdiff(YEAR,`n`.`fecha_nacimiento`,curdate()),' a√±os)') order by `n`.`apellidos` ASC,`n`.`nombre` ASC separator ', ') AS `lista_nadadores` from (`swimmingproject_v1`.`club` `c` left join `swimmingproject_v1`.`nadadores` `n` on((`c`.`id` = `n`.`club_id`))) group by `c`.`id`,`c`.`nombre`,`c`.`ciudad` order by `c`.`nombre`;\n
